import React from "react"

const TodoList = props => {
  const {
    todoList,
    // handleListItemNameChange,
    handleDeleteTodoItem,
    toggleComplete
  } = props

  const list = todoList.filter(listItem => listItem.completed === false)
  return (
    <div className="todolist-group">
      <div className="row container-fluid">
        {list.length === 1
          ? `${list.length} thing to do`
          : `${list.length} things to do`}
      </div>
      {todoList.map(todoItem => (
        <div className="row row-grid container-fluid " key={todoItem.id}>
          <input
            type="text"
            className="form-control col-6"
            id={todoItem.id}
            value={todoItem.name}
            // onChange={handleListItemNameChange}
          />
          <button
            type="button"
            className={
              todoItem.completed
                ? "btn btn-success col-3"
                : "btn btn-outline-success col-3"
            }
            value={todoItem.id}
            onClick={toggleComplete.bind(this, todoItem.id)}
          >
            {todoItem.completed ? `Completed` : `Complete`}
          </button>
          <button
            type="button"
            className="btn btn-secondary col-2"
            value={todoItem.id}
            onClick={handleDeleteTodoItem.bind(this, todoItem.id)}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  )
}

export default TodoList
