import React, { Component } from "react"
import TodoListItem from "./todoListItem"

export default class TodoList extends Component {
  constructor(props) {
    super(props)
    this.state = {}

    this.handleDeleteTodoItem = this.handleDeleteTodoItem.bind(this)
  }

  handleDeleteTodoItem(e) {
    e.preventDefault()
    const todoItemId = e.target.value
    const todoList = this.props.todoList.filter(
      todoItem => todoItem.id !== todoItemId
    )
    console.log(todoList)
    this.props = { ...this.props, todoList: todoList }
  }

  render() {
    const todoCount = this.props.todoList.length
    return (
      <div>
        {todoCount === 1
          ? `${todoCount} thing to do`
          : `${todoCount} things to do`}
        <ol>
          {this.props.todoList.map(todoItem => (
            <TodoListItem
              key={todoItem.id}
              todoItemId={todoItem.id}
              todoItemName={todoItem.name}
              handleDeleteTodoItem={this.handleDeleteTodoItem}
            />
          ))}
        </ol>
      </div>
    )
  }
}
