import React, { Component } from "react"
import TodoListItem from "./todoListItem"

export default class TodoList extends Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  listLength(props) {
    return props.todoList.length
  }

  render() {
    return (
      <div>
        {this.listLength === 1
          ? `${this.listLength} thing to do`
          : `${this.listLength} things to do`}
        <ol>
          {this.props.todoList.map(todoItem => (
            <TodoListItem key={todoItem.id} todoItem={todoItem.name} />
          ))}
        </ol>
      </div>
    )
  }
}
