import React from "react"

const TodoList = props => {
  const { todoList, handleDeleteTodoItem, toggleComplete } = props
  return (
    <div className="col-12 container-fluid">
      {todoList.length === 1
        ? `${todoList.length} thing to do`
        : `${todoList.length} things to do`}
      <div className="col-12 container-fluid">
        {todoList.map(todoItem => (
          <div className="col-12 row" key={todoItem.id}>
            <div className="col-1">{todoItem.name}</div>
            <div className="col-1">
              <button
                type="button"
                className="btn-block btn-secondary"
                value={todoItem.id}
                onClick={handleDeleteTodoItem.bind(this, todoItem.id)}
              >
                Delete
              </button>
            </div>
            <div className="col-1">
              <button
                type="button"
                className={
                  todoItem.completed
                    ? "btn-block btn-outline-success"
                    : "btn-block btn-success"
                }
                value={todoItem.id}
                onClick={toggleComplete.bind(this, todoItem.id)}
              >
                {todoItem.completed ? `Completed` : `Complete`}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

export default TodoList
