import React from "react"

const TodoList = props => {
  const { todoList, handleDeleteTodoItem, toggleComplete } = props
  return (
    <div className="todolist-group">
      <div className="row container-fluid">
        {todoList.length === 1
          ? `${todoList.length} thing to do`
          : `${todoList.length} things to do`}
      </div>
      {todoList.map(todoItem => (
        <div className="row container-fluid col-12 " key={todoItem.id}>
          <input
            type="text"
            className="form-control col-4"
            id="todoItem"
            placeholder="Starting"
            value={todoItem.name}
          />
          {/* <div className="col-2">{todoItem.name}</div> */}
          <div className="col-2">
            <button
              type="button"
              className="btn btn-secondary container-fluid"
              value={todoItem.id}
              onClick={handleDeleteTodoItem.bind(this, todoItem.id)}
            >
              Delete
            </button>
          </div>
          <div className="col-2">
            <button
              type="button"
              className={
                todoItem.completed
                  ? "btn btn-success container-fluid"
                  : "btn btn-outline-success container-fluid"
              }
              value={todoItem.id}
              onClick={toggleComplete.bind(this, todoItem.id)}
            >
              {todoItem.completed ? `Completed` : `Complete`}
            </button>
          </div>
        </div>
      ))}
    </div>
  )
}

export default TodoList
