import React, { Component } from "react"
import TodoListItem from "./todoListItem"

export default class TodoList extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todoList: this.props.todoList,
      todoCount: this.props.todoList.length
    }
  }

  componentDidUpdate() {
    this.setState({
      todoCount: this.state.todoCount
    })
  }

  render() {
    return (
      <div>
        {this.state.todoCount === 1
          ? `${this.state.todoCount} thing to do`
          : `${this.state.todoCount} things to do`}
        <ol>
          {this.props.todoList.map(todoItem => (
            <TodoListItem
              key={todoItem.id}
              todoItemId={todoItem.id}
              todoItemName={todoItem.name}
              delete={this.props.handleDelete}
            />
          ))}
        </ol>
      </div>
    )
  }
}
